import React from 'react';
import { StyleSheet, TextInput, View, Keyboard, Button } from "react-native";
import TypeBadge from './TypeBadge';
import Pokeball from '../../images/vectors/patterns/Pokeball.svg';
import SixByThree from '../../images/vectors/patterns/6x3.svg'

export default function Pokemon(props) {
    console.log(props);
    function formatId(id) {
        var num = '' + id;
        while (num.length < 3){
            num = '0' + num;
        }
        num = '#'+num;
        return num;
    }
    return (
    //    console.log(props.sprites.other["official-artwork"].front_default),
        <View style={styles.container}>
          <View style={[styles.card, {backgroundColor: globalStyles["backgroundtype"+props.types[0].type.name]}]}>
                <Image style={styles.pokeball} source={Pokeball}/>
                <Image style={styles.dotsSixByThree} source={DotsSixByThree} />           
                <Image onError={(e) => console.log(e.nativeEvent.error) } style={styles.sprite} source={{uri:`${props.sprites.other["official-artwork"].front_default}`}} />
                <Image source="https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/1.png" />
            <View style={styles.cardData}>
                <Text style={styles.cardId}>{formatId(props.id)}</Text>
                <Text style={styles.cardName}>{props.name}</Text>
                <View style={styles.cardTypes}>
                <TypeBadge type={props.types[0].type.name} />
                        {props.types.length === 2 && 
                        <TypeBadge type={props.types[1].type.name} />
                        }
                </View>
            </View>
            
            
            </View>
        </View>
    )
}
