// List.js
import React from "react";
import {
  StyleSheet,
  Text,
  View,
  FlatList,
  SafeAreaView,
} from "react-native";

// definition of the Item, which will be rendered in the FlatList
const Item = ({ item }) => (
    console.warn(item),
    <View styles={styles.item}>
        <Text styles={styles.title}>{item.name}</Text>
    <View style={styles.pokeCard}>
        <View style={styles.card}>
            <View style={styles.pokeball}>
                {/* TODO: Pokeball Image here */}
            </View>
            <View styles={styles.sixbythree}>
                {/* TODO: Dots Image here */}
            </View>
            <new Image styles={styles.sprite} source={item.sprites.front_default} />
        
        
        
        </View>
    </View>
    </View>
);

// the filter
const List = ({ searchPhrase, setClicked, data }) => {
  const renderItem = ({ item }) => {
    // when no input, show all
    if (searchPhrase === "") {
      return <><Item item={item} /></>;
    }
    // filter of the name
    if (item.name.includes(searchPhrase.toUpperCase().trim().replace(/\s/g, ""))) {
      return <><Item item={item} /></>;
    }
    // filter of the description
    // if (item.details.toUpperCase().includes(searchPhrase.toUpperCase().trim().replace(/\s/g, ""))) {
    //   return <Item name={item.name} details={item.details} />;
    // }
  };

  return (
    <SafeAreaView style={styles.list__container}>
      <View
        onStartShouldSetResponder={() => {
          setClicked(false);
        }}
      >
        <FlatList
          data={data}
          renderItem={renderItem}
          keyExtractor={(item) => item.id}
        />
      </View>
    </SafeAreaView>
  );
};

export default List;

const styles = StyleSheet.create({
  list__container: {
    margin: 10,
    height: "85%",
    width: "100%",
  },
  item: {
    margin: 30,
    borderBottomWidth: 2,
    borderBottomColor: "lightgrey"
  },
  title: {
    fontSize: 20,
    fontWeight: "bold",
    marginBottom: 5,
    fontStyle: "italic",
  },
  
  pokeCard: {

  },

  card: {

  },

  pokeball: {

  },
  sixbythree: {

  },
  sprite: {

  }
});
