import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import globalStyles from './../../../styles/globalStyles.js';
import DrawerHeader from './DrawerHeader.js';
import GenerationTile from './GenerationTile.js';
const Generations = (props) => {
    return (
        <View style={styles.container}>
            <DrawerHeader
                title="Generations"
                description="Use search for generations to explore your PokÃ©mon!"
            />
            <View style={styles.tilesContainer}>
                <View style={styles.tile}><GenerationTile generationId={1} /></View>
                <View style={styles.tile}><GenerationTile generationId={2} /></View>
                <View style={styles.tile}><GenerationTile generationId={3} /></View>
                <View style={styles.tile}><GenerationTile generationId={4} /></View>
                <View style={styles.tile}><GenerationTile generationId={5} /></View>
                <View style={styles.tile}><GenerationTile generationId={6} /></View>
                <View style={styles.tile}><GenerationTile generationId={7} /></View>
                <View style={styles.tile}><GenerationTile generationId={8} /></View>
            </View>
        </View>
    )
}

export default Generations;

const styles = StyleSheet.create({
    container: {
        marginHorizontal: 40,
        marginTop: 5
    },

    drawerTitle: {
        fontSize: 26,
        color: globalStyles.textblack
    },

    drawerDesc: {
        fontSize: 16,
        color: globalStyles.textgrey
    },

    tilesContainer: {
        marginHorizontal: "auto",
        width: 400,
        flexDirection: "row",
        flexWrap: "wrap"
    },
    tile: {
        flex: 1,
        minWidth: 167,
        maxWidth: 167,
        height: 136,
        justifyContent: "center",
        alignItems: "center",
        paddingBottom: 25
    }
})
