// import { StatusBar } from 'expo-status-bar';
import React, { useEffect, useState  } from 'react';;
import { StatusBar, StyleSheet, Text, View } from 'react-native';
import globalStyles from './styles/globalStyles.js';
import Home from './components/Home/Home.js';
import Profile from './components/Profile/Profile.js';
import { useFonts } from 'expo-font';
import { MainClient } from 'pokenode-ts';


export default function App() {
  const [allPokemonData, setAllPokemonData] = useState([]);
  const [pokemonProfile, setPokemonProfile] = useState('');
  const [pokemonLoaded, setPokemonLoaded] = useState(false);
  const [loaded] = useFonts({
    Inter: require('./fonts/Inter.ttf'),
  });

   // get data from the fake api endpoint
   useEffect(() => {
    console.log("pokemonLoaded: ", pokemonLoaded);
    if (!pokemonLoaded) {
      const abortController = new AbortController()   
      console.log("Oh no");
      var pokemonToAdd = [];
      const getAllPokemonNames = async () => {
          const api = new MainClient();
          fetch(api.pokemon, { signal: abortController.signal})  
              .then((data) => {
                  const getAllPokemonData = async () => {                  
                      for (let i = 1; i < 460; i++){
                        const abortControllerAll = new AbortController()   
                          fetch(api.pokemon.getPokemonById(i),{ signal: abortControllerAll.signal})  

                              .then((data) => {
                                // pokemonToAdd((pokemonToAdd) => ([...pokemonToAdd,data]));
                                pokemonToAdd.push(data);
                                  // console.log(data);
                              })
                              .catch(error => {
                                console.log("Uh oh, error");
                                if (error.name === 'AbortError') return     // if the query has been aborted, do nothing
                                throw error
                              })
                      } 
                      setAllPokemonData(pokemonToAdd);
                      return () => {
                        abortControllerAll.abort() 
                      }
                  } 
                  getAllPokemonData();
              })
              .catch(error => {
                console.log("Uh oh, another error");
                if (error.name === 'AbortError') return     // if the query has been aborted, do nothing
                throw error
              })

      }  

      getAllPokemonNames();
      setPokemonLoaded(true);
      return () => {
        abortController.abort() 
      }

      // console.log({allPokemonData});
    }

}, []);

useEffect( () => {
  console.log("pokemonLoaded: ", pokemonLoaded);;
}, [pokemonLoaded]);

  if (!loaded) {
    return null;
  }



  return (
    <>
        <View style={styles.container}>
          {(pokemonProfile === '') ? (<Home setPokemonProfile={setPokemonProfile} allPokemonData={allPokemonData} /> ) : ( <Profile setPokemonProfile={setPokemonProfile} mon={pokemonProfile} /> ) }
        </View>
    </>
  )
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'flex-start',
    paddingTop: StatusBar.currentHeight,
    fontFamily: 'serif'
  }
})
