import React from 'react';
import {  StyleSheet, View, Image, Text } from "react-native";
import globalStyles from '../styles/globalStyles.js';

export default function TypeBadge(props) {
    // {props.type} = fire

    const bkgdName = "backgroundtype"+props.type;
    console.log(bkgdName); //logs as backgroundtypefire

    const bkgdColor = globalStyles[bkgdName];
    console.log(bkgdColor); //logs as [object Object].backgroundtypefire

return (
        console.log(props),
        <View style={styles.typebadge}>
            {/* <Image className="typebadge-icon" src={require(`../images/vectors/types/${props.type}.svg`)} style={{  color:"var(--text-white)"}}/> */}
            <Text>{props.type}</Text>
        </View>
    )
}

const styles = StyleSheet.create({
    typebadge: {
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'center',
        padding: 5,
        gap: 5,
        backgroundColor: bkgdColor,
    
        width: 'max-content',
        height: 25,
        borderRadius: 3,
        marginVertical: 5,
        marginHorizontal: 2,
        zIndex: 0,
      },
      
      icon: {
        width: 15,
        height: 15,
    
        /* Cant seem to use fill, color or background-color. 
        Use https://codepen.io/sosuke/pen/Pjoqqp to match colors for filter.
        This is colour matched to --text-white. */
        filter: 'invert(99%) sepia(0%) saturate(2%) hue-rotate(224deg) brightness(101%) contrast(100%)'
      },
      
      name: {
        color: globalStyles.textwhite,
        textTransform: capitalize,
        fontWeight: '500',
        fontSize: 12,
        lineHeight: 14,
      }
})
