import React, { useState, useEffect, startTransition } from 'react';
import { StatusBar, Image, View, TextInput, Text, StyleSheet, ActivityIndicator, Dimensions } from 'react-native';
import PokeballTop from '../../images/vectors/patterns/PokeballTop.png';
import TypeBadge from '../Home/TypeBadge';
import globalStyles from '../../styles/globalStyles.js';

const Profile = (props) => {
    function formatId(id) {
        var num = '' + id;
        while (num.length < 3){
            num = '0' + num;
        }
        num = '#'+num;
        return num;
    }
    return (
        <View style={[styles.container, {backgroundColor: globalStyles["backgroundtype"+props.mon.types[0].type.name]}]}>
            <View style={styles.Profile}> 
            <Text>The Pokemon name is {props.mon.name}</Text>
            <Text>The Pokemon ID is {props.mon.id}</Text>
            <Text>The Pokemon type 1 is {props.mon.types[0].type.name}</Text>
            <View style={styles.cardData}>
                <Text style={styles.cardId}>{formatId(props.mon.id)}</Text>
                <Text style={styles.cardName}>{props.mon.name}</Text>
                <View style={styles.cardTypes}>
                <TypeBadge type={props.mon.types[0].type.name} />
                        {props.mon.types.length === 2 && 
                        <TypeBadge type={props.mon.types[1].type.name} />
                        }
                </View>
            </View>
        </View>
        </View>
    );
};

export default Profile;

const styles = StyleSheet.create({

    root: {
        boxSizing: 'border-box',
    },
  
    container: {
        padding: 0,
        margin: 0,
        backgroundColor: globalStyles.backgroundtypenormal
    },

    Navbar: {
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'flex-end',
        marginTop: 2.5,
        marginRight: 2.5,
        marginBottom: 37.5,
        height: 30,
      },

    Profile: {
      display: 'flex',
      flexDirection: 'column',
      paddingTop: StatusBar.currentHeight,
      marginHorizontal: 40,
      width: '100%',
    },

    pokeballTop: {
        position: 'absolute',
        top: StatusBar.currentHeight,
        left: 0,
        width: Dimensions.get('window').width,
        height: Math.round(Dimensions.get('window').width) * (207 / 414),
    },

    title: {
      margin: 0,
      padding: 0
    },
    
    titleText: {
        fontWeight: '700',
        fontSize: 32,
        color: globalStyles.textblack,
        margin: 0,
        marginBottom: 10,
    },
    
    titleDescription: {
      fontWeight: '400',
      fontSize: 16,
      lineHeight: 19,
      color: globalStyles.textgrey,
      margin: 0,
    },

    cardData: {
        display: 'flex',
        flexDirection: 'column',
      },
      
      cardTypes: {
        display: 'flex',
        flexDirection: 'row'
      },
      
      cardId: {
        fontWeight: '700',
        fontSize: 12,
        color: globalStyles.textnumber
      },
      
      cardName: {
        textTransform: 'capitalize',
        fontWeight: '700',
        fontSize: 26,
        color: globalStyles.textwhite
      },
      
      cardImg: {
        position: 'absolute',
        right: 10,
        bottom: 25,
        height: 130,
        width: 130,
      },  
  })
