    console.log(`Variables.${type}`)
return (
        console.log(props),
        <View style={{backgroundColor: vari}}>
            {/* <Image className="typebadge-icon" src={require(`../images/vectors/types/${props.type}.svg`)} style={{  color:"var(--text-white)"}}/> */}
            <Text style={{fontSize: 32, backgroundColor: vari}}>{props.type}</Text>
        </View>
    )
}
