// import { StatusBar } from 'expo-status-bar';
import React, { useEffect, useState  } from 'react';;
import { StatusBar, StyleSheet, Text, View } from 'react-native';
import globalStyles from './styles/globalStyles.js';
import Home from './components/Home/Home.js';
import Profile from './components/Profile/Profile.js';
import { useFonts } from 'expo-font';
import { MainClient } from 'pokenode-ts';


export default function App() {
  const [allPokemonData, setAllPokemonData] = useState([]);
  const [pokemonProfile, setPokemonProfile] = useState('');
  const [pokemonLoaded, setPokemonLoaded] = useState(false);
  const [loaded] = useFonts({
    Inter: require('./fonts/Inter.ttf'),
  });

   // get data from the fake api endpoint
   useEffect(() => {
    console.log("pokemonLoaded: $b", pokemonLoaded);
    if (!pokemonLoaded) {
      var pokemonToAdd = [];
      const getAllPokemonNames = async () => {
          const api = new MainClient();
          await api.pokemon
              .listPokemons(1,5)
              .then((data) => {
                  const getAllPokemonData = async () => {                  
                      for (let i = 1; i < 30; i++){
                          await api.pokemon
                              .getPokemonById(i)
                              .then((data) => {
                                // pokemonToAdd((pokemonToAdd) => ([...pokemonToAdd,data]));
                                pokemonToAdd.push(data);
                                  // console.log(data);
                              })
                      } 
                      setAllPokemonData(pokemonToAdd);

                  } 
                  getAllPokemonData();
              })
      }  
      getAllPokemonNames();
      setPokemonLoaded(true);
      // console.log({allPokemonData});
    }
}, []);

  if (!loaded) {
    return null;
  }



  return (
    <>
        <View style={styles.container}>
          {(pokemonProfile === '') ? (<Home setPokemonProfile={setPokemonProfile} allPokemonData={allPokemonData} /> ) : ( <Profile setPokemonProfile={setPokemonProfile} mon={pokemonProfile} /> ) }
        </View>
    </>
  )
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'flex-start',
    paddingTop: StatusBar.currentHeight,
    fontFamily: 'serif'
  }
})
