import React, { useState, useEffect } from 'react';
import { StyleSheet, TextInput, View, Image, Text, Keyboard, Button } from "react-native";
import Variables from '../styles/Variables';

export default function TypeBadge(props) {
    var color = "'Variables.type'+${props.type}"
return (
        console.log(props),
        <View styles={{backgroundColor: (Variables.type)+(this.props.type)}}>
            {/* <Image className="typebadge-icon" src={require(`../images/vectors/types/${props.type}.svg`)} style={{  color:"var(--text-white)"}}/> */}
            <Text styles={{color: blue}}>{props.type}</Text>
        </View>
    )
}
