// List.js
import React from "react";
import Variables from '../styles/Variables';
import {
  StyleSheet,
  Text,
  View,
  FlatList,
  SafeAreaView,
} from "react-native";

// definition of the Item, which will be rendered in the FlatList
const Item = ({ item }) => (
    console.warn(item),
    <View style={styles.container}>
        <View style={styles.card}>
            <View style={styles.pokeball}>
                {/* TODO: Pokeball Image here */}
            </View>
            <View styles={styles.sixbythree}>
                {/* TODO: Dots Image here */}
            </View>
            <new Image styles={styles.sprite} source={item.sprites.front_default} />
        <View styles={styles.cardData}>
            <Text styles={styles.cardId}>{item.id}</Text>
            <Text styles={styles.cardName}>{item.name}</Text>
        </View>
        
        
        </View>
    </View>
);

// the filter
const List = ({ searchPhrase, setClicked, data }) => {
  const renderItem = ({ item }) => {
    // when no input, show all
    if (searchPhrase === "") {
      return <><Item item={item} /></>;
    }
    // filter of the name
    if (item.name.includes(searchPhrase.toUpperCase().trim().replace(/\s/g, ""))) {
      return <><Item item={item} /></>;
    }
    // filter of the description
    // if (item.details.toUpperCase().includes(searchPhrase.toUpperCase().trim().replace(/\s/g, ""))) {
    //   return <Item name={item.name} details={item.details} />;
    // }
  };

  return (
    <SafeAreaView style={styles.list__container}>
      <View
        onStartShouldSetResponder={() => {
          setClicked(false);
        }}
      >
        <FlatList
          data={data}
          renderItem={renderItem}
          keyExtractor={(item) => item.id}
        />
      </View>
    </SafeAreaView>
  );
};

export default List;

const styles = StyleSheet.create({
  list__container: {
    margin: 10,
    height: "85%",
    width: "100%",
  },
  item: {
    margin: 30,
    borderBottomWidth: 2,
    borderBottomColor: "lightgrey"
  },
  title: {
    fontSize: 20,
    fontWeight: "bold",
    marginBottom: 5,
    fontStyle: "italic",
  },
  
  pokeCard: {
    position: 'relative',
    margin: 0,
  },

  card: {
    display: 'flex',
    borderRadius: 10,
    margin: 15,
    height: 115,
    padding: 20,
  },

  pokeball: {
    position: 'absolute',
    right: -15,
    bottom: 0,
    height: 145,
    width: 145,
  },
  sixbythree: {
    position: 'absolute',
    top: 25,
    left: 90,
    width: 74,
    height: 32,
  },
  sprite: {

  },
  
  cardData: {
    display: 'flex',
    flexDirection: 'column',
  },
  
  cardTypes: {
    display: 'flex',
    flexDirection: 'row'
  },
  
  cardId: {
    fontWeight: 700,
    fontSize: 12,
    lineHeight: 14,
    color: Variables.textnumber,
    margin: 0,
  },
  
  cardName: {
    textTransform: 'capitalize',
    fontWeight: 700,
    fontSize: 26,
    lineHeight: 31,
    color: Variables.textwhite,
    margin: 0,
    zIndex: 0,
  },
  
  cardImg: {
    position: 'absolute',
    right: 10,
    bottom: 25,
    height: 130,
    width: 130,
  },

//   @media screen and (max-width: 320px) {
//     .card-img, .card-sixbythree {
//       display: 'none';
//     }
//   }


});
