import React, { useState, useEffect } from 'react';
import { Image, ScrollView, View, TextInput, Text } from 'react-native';
import IconSearch from '../images/vectors/icons/Search.svg';
import { pokemonClient } from 'pokenode-ts';

import generation from '../images/vectors/icons/Generation.svg';
import sort from '../images/vectors/icons/Sort.svg';
import filter from '../images/vectors/icons/Filter.svg';

const Home = () => {
    const p = new pokedex();
    const [searchTerm, setSearchTerm] = useState('');
    const [fullpokemonList, setFullpokemonList] = useState('');
    const [displayedPokemonList, setDisplayedpokemonList] = useState('');

    const [homeOptions, setHomeOptions] = useState('');

    // const handleKeyDown = (event) => {
    //     if (event.key === 'Enter') {
    //         searchpokemon(searchTerm)
    //     }
    // }
    
    // const searchpokemon = async (name) => {
    //     p.getpokemonByName([name.toLowerCase()]) // with promise
    //     .then((response) => {
    //         console.log(response);
    //         setpokemon(response[0]);
    //     })
    //     .catch((error) => {
    //         console.log('There was an ERROR: ', error);
    //     });
    // }
    
    const interval = {
        limit: 1,
        offset: 0
    }

    useEffect(() => {
        p.getpokemonsList(interval)
            .then((response) => {
                setFullpokemonList(response.results.map(mon => {
                    return <HomepokeCard name={mon.name} />
            }))
            console.log(fullpokemonList);
        })

    }, []);

    useEffect(() => {
        try {
            setDisplayedpokemonList(fullpokemonList.map(mon => {
            if (searchTerm.length <= 2) {
                return <HomepokeCard name={mon.props.name} />
            }
            if (searchTerm.length > 2 && mon.props.name.includes(searchTerm)) {
                return <HomepokeCard name={mon.props.name} />
            }
        }))
    }
    catch {
        p.getpokemonsList(interval)
            .then((response) => {
                setDisplayedpokemonList(response.results.map(mon => {

                    return <HomepokeCard name={mon.name} />
            }))
    })
    
    }
    }, [searchTerm]);

    // useEffect(() => {
    //     if (searchTerm != "" && searchTerm.length > 2) {
    //         console.log(fullpokemonList);
    //         fullpokemonList.map(mon => {
    //             if (mon.props.name.includes(searchTerm)) {

    //                 return <HomepokeCard name={mon.name} />
    //             }
    //         })
    //         console.log(searchTerm);
    //         console.log(searchedpokemonList);
    //     }
    // }, [searchTerm]);
   

    return (
        <View style={styles.Home}>
            <View style={styles.HomeNav}>
                <View>
                    <Image style={styles.navGeneration} src={generation} alt="generation" onClick={() => setHomeOptions('generation')} />
                    <Image style={styles.navSort} src={sort} alt="sort" onClick={() => setHomeOptions('sort')} />
                    <Image style={styles.navFilter} src={filter} alt="filter" onClick={() => setHomeOptions('filter')} />
                </View>
            </View>
            <View>
                <View style={styles.title}>
                    <Text style={styles.titleP}>Pokédex</Text>
                    <Text style={styles.titleDescription}>Search for Pokémon by name or using the National Pokédex number.</Text>
                </View>

                <View style={styles.search}>
                        <Image style={styles.searchIcon} src={IconSearch} alt="Search" />
                        <TextInput
                            style={styles.searchInput}
                            placeholder="What Pokémon are you looking for?"
                            type="Text"
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                </View>

                <ScrollView style={styles.monlist}>
                    {displayedPokemonList} 
                </ScrollView> 
            </View>
        </View>
    )
}

export default Home;
