import React, { useState, useEffect, startTransition } from 'react';
import { TouchableOpacity, ScrollView, Dimensions, StatusBar, Image, View, TextInput, Text, StyleSheet, ActivityIndicator, BackHandler } from 'react-native';
import PokeballTop from '../../images/vectors/patterns/PokeballTop.png';
import TypeBadge from '../Home/TypeBadge';
import globalStyles from '../../styles/globalStyles.js';
import GradientNameText from '../GradientNameText';
import Pokeball from '../../images/vectors/patterns/PokeballTab.png';
import DotsSixByThree from '../../images/vectors/patterns/6x3.png';
import Circle from '../../images/vectors/patterns/Circle.png';
import Back from '../../images/vectors/icons/Back.png';
import { Languages, MainClient } from 'pokenode-ts';

const StatsTable = (props) => {
    console.log(props.stats);
    const [statsTable, setStatsTable] = useState();

    function formatStat(stat) {
        switch(stat) {
            case 'hp': return 'HP';
            case 'attack': return 'Attack';
            case 'defense': return 'Defense';
            case 'special-attack': return 'Sp. Att';
            case 'special-defense': return 'Sp. Def';
            case 'speed': return 'Speed';
            default: return '';
        }
    }
    useEffect(() => {
        const createStatsTable = props.stats.map((stat) => 
            <View key={stat.stat.name} style={styles.rowContainer}>
               
                <Text style={styles.statName}>
                   {formatStat(stat.stat.name)}
                </Text>
                <Text style={styles.statBaseStat}>
                    {stat.base_stat}
                </Text>
                <Text style={styles.statBar}>
                    Stat Bar Here!
                </Text>
                    <Text style={styles.statLow}>
                        {stat.stat.name === 'hp'
                        // HP Calulator: (((2x Base+ IV + (EV/4)*Level)/100)+Level+10)
                        ? ('HP Low')
                        //  Stat Calulator: ((((2*Base+IV+(EV/4)*Level)/100)+5)*Nature) 
                        : (Math.floor(((((2*stat.base_stat+0+(0/4))*100)/100)+5)*0.9))
                        }
                    </Text>
                    <Text style={styles.statHigh}>
                        {stat.stat.name === 'hp'
                        // HP Calulator: (((2x Base+ IV + (EV/4)*Level)/100)+Level+10)
                        ? ('HP High')
                        //  Stat Calulator: ((((2*Base+IV+(EV/4)*Level)/100)+5)*Nature) 
                        : (Math.floor(((((2*stat.base_stat+31+(251/4))*100)/100)+5)*1.1))
                        }
                    </Text>
            </View>
        )
        setStatsTable(createStatsTable);
    },[]);

    return (
        <View style={styles.container}>
            {statsTable}
            <View style={styles.rowContainer}>
            <Text style={styles.statTotal}>
                   Total
                </Text>
                <Text style={styles.statBaseStatTotal}>
                    
                </Text>
                <Text style={styles.statBar}>
                </Text>
                    <Text style={styles.statLow}>
                        Min
                    </Text>
                    <Text style={styles.statHigh}>
                        Max
                    </Text>
            </View>
        </View>
    )
};

export default StatsTable;


const styles = StyleSheet.create({

    container: {
        position: 'flex',
        flexDirection: 'column',
        width: '100%',
        marginBottom: 2.5
    },

    containerTotal: {
        position: 'flex',
        flexDirection: 'row',
        gap: 10
    },

    rowContainer: {
        position: 'flex',
        flexDirection: 'row',
        gap: 10
    },
    statName: {
        width: '20%',
        textTransform: 'capitalize',
        fontSize: 12,
        fontWeight: '500',
        color: globalStyles.textblack
    },

    statBaseStat: {
        width: '9%',
        fontSize: 16,
        fontWeight: '400',
        color: globalStyles.textgrey
    },

    statBar: {
        width: '40%',
    },

    statLow: {
        width: '9%',
        width: '9%',
        fontSize: 16,
        fontWeight: '400',
        color: globalStyles.textgrey
    },

    statHigh: {
        width: '9%',
        width: '9%',
        fontSize: 16,
        fontWeight: '400',
        color: globalStyles.textgrey
    },

})
