import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import globalStyles from './../../../styles/globalStyles.js';


const GenerationTile = (props) => {

    function romanize (num) {
        if (isNaN(num))
            return NaN;
        var digits = String(+num).split(""),
            key = ["","C","CC","CCC","CD","D","DC","DCC","DCCC","CM",
                   "","X","XX","XXX","XL","L","LX","LXX","LXXX","XC",
                   "","I","II","III","IV","V","VI","VII","VIII","IX"],
            roman = "",
            i = 3;
        while (i--)
            roman = (key[+digits.pop() + (i * 10)] || "") + roman;
        return Array(+digits.join("") + 1).join("M") + roman;
    }


    const images = [
        { id: 1, image: require("../../../images/generations/Generation 1.png") },
        { id: 2, image: require("../../../images/generations/Generation 2.png") },
        { id: 3, image: require("../../../images/generations/Generation 3.png") },
        { id: 4, image: require("../../../images/generations/Generation 4.png") },
        { id: 5, image: require("../../../images/generations/Generation 5.png") },
        { id: 6, image: require("../../../images/generations/Generation 6.png") },
        { id: 7, image: require("../../../images/generations/Generation 7.png") },
        { id: 8, image: require("../../../images/generations/Generation 8.png") },
    ]

    return (
        <View style={styles.container}>
            <Image source={images[props.generationId]} />
            <Text style={styles.title}>Generation {romanize(props.generationId)}</Text>
        </View>
    )
}

export default GenerationTile;

const styles = StyleSheet.create({
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10,
        backgroundColor: globalStyles.backgrounddefaultinput,
        height: 129,
        width: 160

    },

    title: {
        fontSize: 16,
        color: globalStyles.textgrey
    }

})
