//                     } 
//                     getAllPokemonData();
//                 })
//         }  
//         getAllPokemonNames();
//         // console.log({allPokemonData});
//     }, []);

    useEffect(() => {
        console.log("Drawer option is now %s", drawerOption);
    }, [drawerOption]);
    return (
        <>
        <Image style={styles.pokeballTop} source={PokeballTop} />
        <View style={styles.container}>
            <View style={styles.Home}> 
                <View>
                    <View>
                     <Navbar style={styles.Navbar} setDrawerOption={setDrawerOption} />
                    </View>
                    <View style={styles.title}>
                        <Text style={styles.titleText}>Pokédex</Text>
                        <Text style={styles.titleDescription}>Search for Pokémon by name or using the National Pokédex number.</Text>
                    </View>

                    <View style={styles.search}>
                        <SearchBar
                            searchPhrase={props.searchPhrase}
                            setSearchPhrase={props.setSearchPhrase}
                            clicked={clicked}
                            setClicked={setClicked}
                        />
                        <List 
                            loading={props.loading}
                            searchPhrase={props.searchPhrase}
                            setSearchPhrase={props.setSearchPhrase}
                            setClicked={setClicked}
                            setPokemonProfile={props.setPokemonProfile}
                            filteredData={filteredData}
                        />
                        {/* <Options /> */}
                    </View>
                </View>
                <Drawer 
                    isVisible={true}
                    onClose={onClose}
                    >
                        <Text>THIS IS THE DRAWER!</Text>
                    </Drawer>

            </View>
        </View>
        </>

    );
};

export default Home;

