import React, { useState, useEffect } from 'react';
import { StyleSheet, TextInput, View, Image, Text, Keyboard, Button, ViewPagerAndroidBase } from "react-native";
import Variables from '../styles/Variables';

export default function TypeBadge(props) {
    const vari = "Variables.type";
    var thingColor = `${vari}${props.type}`;
return (
        console.log(props),
        <View style={{backgroundColor: thingColor}}>
            {/* <Image className="typebadge-icon" src={require(`../images/vectors/types/${props.type}.svg`)} style={{  color:"var(--text-white)"}}/> */}
            <Text style={{backgroundColor: thingColor}}>{props.type}</Text>
        </View>
    )
}
