// import { StatusBar } from 'expo-status-bar';
import React, { useState, useEffect } from 'react';;
import { ScrollView, StatusBar, StyleSheet, Text, View } from 'react-native';
import globalStyles from './styles/globalStyles.js';
import Home from './components/Home/Home.js';
import Profile from './components/Profile/Profile.js';
import { useFonts } from 'expo-font';


export default function App() {
  const [pokemonProfile, setPokemonProfile] = useState('');
  const [homeOption, setHomeOption] = useState('');
  const [isScrollEnabled, setIsScrollEnabled] = useState(true);

  useEffect(() => {
    console.log("Home option is now %s", homeOption);
    homeOption === '' ? setIsScrollEnabled(true) : setIsScrollEnabled(false);
    console.log("isScrollEnabled is now %s", isScrollEnabled);
}, [homeOption]);

  const [loaded] = useFonts({
    Inter: require('./fonts/Inter.ttf'),
  });
  if (!loaded) {
    return null;
  }


  return (
        <View scrollEnabled={isScrollEnabled} nestedScrollEnabled={true} style={styles.container}>
          {(pokemonProfile === '') 
          ? (<Home 
              setPokemonProfile={setPokemonProfile}
              homeOption={homeOption}
              setHomeOption={setHomeOption}
              isScrollEnabled={isScrollEnabled}
              setIsScrollEnabled={setIsScrollEnabled} /> ) 
          : ( <Profile setPokemonProfile={setPokemonProfile} mon={pokemonProfile} /> ) }
        </View>

  )
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#fff',
    // alignItems: 'center',
    // justifyContent: 'flex-start',
    paddingTop: StatusBar.currentHeight,
    fontFamily: 'serif'
  }
})
