import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import globalStyles from './../../../styles/globalStyles.js';


const GenerationTile = (props) => {

    function romanize (num) {
        if (isNaN(num))
            return NaN;
        var digits = String(+num).split(""),
            key = ["","C","CC","CCC","CD","D","DC","DCC","DCCC","CM",
                   "","X","XX","XXX","XL","L","LX","LXX","LXXX","XC",
                   "","I","II","III","IV","V","VI","VII","VIII","IX"],
            roman = "",
            i = 3;
        while (i--)
            roman = (key[+digits.pop() + (i * 10)] || "") + roman;
        return Array(+digits.join("") + 1).join("M") + roman;
    }


    const getImage = (() => {
        switch (props.generationId) {
            case 1: return require("../../../images/generations/Generation 1.png")
            case 2: return require("../../../images/generations/Generation 2.png")
            case 3: return require("../../../images/generations/Generation 3.png")
            case 4: return require("../../../images/generations/Generation 4.png")
            case 5: return require("../../../images/generations/Generation 5.png")
            case 6: return require("../../../images/generations/Generation 6.png")
            case 7: return require("../../../images/generations/Generation 7.png")
            case 8: return require("../../../images/generations/Generation 8.png")
            default: return require("../../../images/generations/Generation 1.png")
        }
    })

    return (
        <View style={styles.container}>
            <Image source={getImage} />
            <Text style={styles.title}>Generation {romanize(props.generationId)}</Text>
        </View>
    )
}

export default GenerationTile;

const styles = StyleSheet.create({
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10,
        backgroundColor: globalStyles.backgrounddefaultinput,
        height: 129,
        width: 160

    },

    title: {
        fontSize: 16,
        color: globalStyles.textgrey
    }

})
