import React, { useState, useEffect, startTransition } from 'react';
import { Dimensions, StatusBar, Image, View, TextInput, Text, StyleSheet, ActivityIndicator } from 'react-native';
import PokeballTop from '../../images/vectors/patterns/PokeballTop.png';
import TypeBadge from '../Home/TypeBadge';
import globalStyles from '../../styles/globalStyles.js';
import GradientNameText from '../GradientNameText';

const Profile = (props) => {
    function formatId(id) {
        var num = '' + id;
        while (num.length < 3){
            num = '0' + num;
        }
        num = '#'+num;
        return num;
    }


    
    return (
        <View style={[styles.container, {backgroundColor: globalStyles["backgroundtype"+props.mon.types[0].type.name]}]}>
            <View style={styles.pokemonTitleContainerContainer}>
                <View style={styles.pokemonTitleContainer}>
                    <GradientNameText style={styles.pokemonTitle} color={globalStyles["backgroundtype"+props.mon.types[0].type.name]} name={props.mon.name}/>
                </View>
            </View>
            <View style={styles.Profile}> 
                <View style={styles.top}>
                    <View style={styles.cardData}>
                        <Text style={styles.cardId}>{formatId(props.mon.id)}</Text>
                        <Text style={styles.cardName}>{props.mon.name}</Text>
                        <View style={styles.cardTypes}>
                            <TypeBadge type={props.mon.types[0].type.name} />
                                    {props.mon.types.length === 2 && 
                                    <TypeBadge type={props.mon.types[1].type.name} />
                                    }
                        </View>
                    </View>
                </View>  
            </View>
        </View>
    );
};

export default Profile;

const styles = StyleSheet.create({

    root: {
        boxSizing: 'border-box',
    },
  
    container: {
        padding: 0,
        margin: 0,
        width: '100%',
        backgroundColor: globalStyles.backgroundtypenormal
    },

    pokemonTitleContainer: {
        position: 'absolute',
        top: 25,
        left:0,
        height: 114,
        width: Dimensions.get('window').width,
    },

    pokemonTitle: {
        position: 'relative',
        textTransform: 'capitalize',
        fontWeight: '700',
        fontSize: 100,
        alignSelf: 'center',
        color: 'rgba(255, 255, 255, 1.0)',
        fontFamily: 'Outline'
    },

    Navbar: {
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'flex-end',
        marginTop: 2.5,
        marginRight: 2.5,
        marginBottom: 37.5,
        height: 30,
      },

    Profile: {
      display: 'flex',
      flexDirection: 'column',
      paddingTop: StatusBar.currentHeight,
      marginHorizontal: 40,
      marginTop: 120
    },
    
    top: {
        display: 'flex',
        flexDirection: 'row',
        borderRadius: 10,
        marginTop: 30,
        height: 115,
        padding: 0,
        backgroundColor: globalStyles.typenormal,
    },

    pokeballTop: {
        position: 'absolute',
        top: StatusBar.currentHeight,
        left: 0,
        width: Dimensions.get('window').width,
        height: Math.round(Dimensions.get('window').width) * (207 / 414),
    },

    title: {
      margin: 0,
      padding: 0
    },
    
    titleText: {
        fontWeight: '700',
        fontSize: 32,
        color: globalStyles.textblack,
        margin: 0,
        marginBottom: 10,
    },
    
    titleDescription: {
      fontWeight: '400',
      fontSize: 16,
      lineHeight: 19,
      color: globalStyles.textgrey,
      margin: 0,
    },

    cardData: {
        display: 'flex',
        flexDirection: 'column',
        maxWidth: '90%'
      },
      
      cardTypes: {
        display: 'flex',
        flexDirection: 'row'
      },
      
      cardId: {
        fontWeight: '700',
        fontSize: 12,
        color: globalStyles.textnumber
      },
      
      cardName: {
        textTransform: 'capitalize',
        fontWeight: '700',
        fontSize: 26,
        color: globalStyles.textwhite
      },
      
      cardImg: {
        position: 'absolute',
        right: 10,
        bottom: 25,
        height: 130,
        width: 130,
      },  
  })
