// List.js
import React from "react";
import {
  StyleSheet,
  Text,
  View,
  FlatList,
  SafeAreaView,
} from "react-native";

// definition of the Item, which will be rendered in the FlatList
const Item = ({ mon }) => (
    console.warn(mon[0].name),
    <View styles={styles.item}>
        <Text styles={styles.title}>Hello there</Text>
        <Text styles={styles.title}>{mon.name}</Text>
    </View>
//   <View style={styles.pokeCard}>
//     <View style={styles.card}>
//         <View style={styles.pokeball}>
//             {/* TODO: Pokeball Image here */}
//         </View>
//         <View styles={styles.sixbythree}>
//             {/* TODO: Dots Image here */}
//         </View>
//         <Image styles={styles.sprite} source={mon.sprites.other["official-artwork"].front_default} />
//     </View>
//   </View>
);

// the filter
const List = ({ searchPhrase, setClicked, data }) => {
  const renderItem = (mon) => {
    // when no input, show all
    if (searchPhrase === "") {
      return <><Item mon={data} /></>;
    }
    // filter of the name
    if (mon.name.includes(searchPhrase.toUpperCase().trim().replace(/\s/g, ""))) {
      return <><Item mon={data} /></>;
    }
    // filter of the description
    // if (item.details.toUpperCase().includes(searchPhrase.toUpperCase().trim().replace(/\s/g, ""))) {
    //   return <Item name={item.name} details={item.details} />;
    // }
  };

  return (
    <SafeAreaView style={styles.list__container}>
      <View
        onStartShouldSetResponder={() => {
          setClicked(false);
        }}
      >
        <FlatList
          data={data}
          renderItem={renderItem}
          keyExtractor={(mon) => mon.id}
        />
      </View>
    </SafeAreaView>
  );
};

export default List;

const styles = StyleSheet.create({
  list__container: {
    margin: 10,
    height: "85%",
    width: "100%",
  },
  item: {
    margin: 30,
    borderBottomWidth: 2,
    borderBottomColor: "lightgrey"
  },
  title: {
    fontSize: 20,
    fontWeight: "bold",
    marginBottom: 5,
    fontStyle: "italic",
  },
  
  pokeCard: {

  },

  card: {

  },

  pokeball: {

  },
  sixbythree: {

  },
  sprite: {

  }
});
